import { makeAutoObservable } from "mobx"


export default class UserStore {
  constructor() {
    this._isAuth = false
    this._user = {}
    makeAutoObservable(this)
  }

  setIsAuth(bool) {
    this._isAuth = bool
  }
  setUser(uesr) {
    this._user = bool
  }

  get isAuth() {
    return this._isAuth
  }
  get user() {
    return this._user
  }
}
